import React from 'react'
import axios from 'axios'

function Chatbot(){

    const textQuery= async(text) => {
        let conversation = {
            who: 'user',
            content: {
                text: {
                    text: text
                }
            }
        }

        console.log(conversation)

        // const textQueryInput = {text}

        try {
            const response = await axios.post('/api/dialogflow/textQuery', text)
            const content = response.data.fullfillmentMessages[0]
            conversation = {
                who: 'bot',
                content: content
            }
            console.log(conversation)
        } catch (error) {
            conversation = {
                who: 'bot',
                content: {
                    text: {
                        text: 'Yikes, woe is me!  Something went wrong.'
                    }
                }
            }
            console.log(conversation)
        }
    }

    const keyPressHandler = (event) => {
        if (event.key === "Enter") {
            if (event.target.value) {
                return alert('Sorry, you need to type something first!')
            }
            textQuery(event.target.value)
            event.target.value = ""
        }
    }

    return (
        <div style={{
            height: 700, width: 700,
            border: '3px solid black', borderRadius: '7px'
        }}>
            <div style={{ height: 644, width: '100%', overflow: 'auto' }}>



            </div>
            <input
                style={{
                    margin: 0, width: '100%', height: 50,
                    borderRadius: '4px', padding: '5px', fontSize: '1rem'
                }}
                placeholder="Send a message..."
                onKeyPress={keyPressHandler}
                type="text"
            />

        </div>
    )
}

export default Chatbot
